empty &#x3D;&#x3D;&#x3D; null; &#x2F;&#x2F; true
&#x2F;&#x2F; or
Object.prototype.toString.call(empty); &#x2F;&#x2F; &#39;[object Null]&#39;
&#x2F;&#x2F; or
!empty &amp;&amp; typeof empty &#x3D;&#x3D;&#x3D; &#39;object&#39;; &#x2F;&#x2F; true

